import React, { useState, useRef, useEffect } from "react";
import {
  Box,
  Typography,
  Paper,
  TextField,
  IconButton,
  CircularProgress,
  createTheme,
  ThemeProvider
} from "@mui/material";
import SendIcon from "@mui/icons-material/Send";
import MicIcon from "@mui/icons-material/Mic";
import ImageIcon from "@mui/icons-material/Image";
import ReactMarkdown from "react-markdown";

// Custom MUI theme with medical green palette
const theme = createTheme({
  palette: {
    primary: {
      main: "#4caf50",  // Green
      contrastText: "#fff"
    },
    secondary: {
      main: "#388e3c",  // Darker green
    },
    background: {
      default: "#f5f7fa",
      paper: "#fff"
    }
  },
  typography: {
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
});

const ChatBubble = ({ sender, text }) => {
  const safeText = typeof text === "string" ? text : String(text);

  return (
    <Paper
      elevation={3}
      sx={{
        maxWidth: "70%",
        p: 2,
        mb: 1,
        bgcolor: sender === "user" ? "primary.main" : "grey.100",
        color: sender === "user" ? "primary.contrastText" : "text.primary",
        alignSelf: sender === "user" ? "flex-end" : "flex-start",
        borderRadius: 2,
        wordBreak: "break-word",
        whiteSpace: "pre-line"
      }}
    >
      {sender === "bot" ? (
        <ReactMarkdown>{safeText}</ReactMarkdown>
      ) : (
        safeText
      )}
    </Paper>
  );
};

export default function MaterialChatbot() {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(false);
  const [image, setImage] = useState(null);
  const [voiceActive, setVoiceActive] = useState(false);
  const recognitionRef = useRef(null);
  const chatRef = useRef(null);

  useEffect(() => {
    if (chatRef.current) {
      chatRef.current.scrollTop = chatRef.current.scrollHeight;
    }
  }, [messages, loading]);

  useEffect(() => {
    if ("webkitSpeechRecognition" in window) {
      const rec = new window.webkitSpeechRecognition();
      rec.lang = "en-US";
      rec.continuous = false;
      rec.interimResults = false;
      rec.onstart = () => setVoiceActive(true);
      rec.onend = () => setVoiceActive(false);
      rec.onerror = () => setVoiceActive(false);
      rec.onresult = (e) => {
        const transcript = e.results[0][0].transcript;
        handleTextSend(transcript);
      };
      recognitionRef.current = rec;
    }
  }, []);

  const appendMessage = (sender, text) => {
    setMessages((prev) => [...prev, { sender, text }]);
  };

  const handleTextSend = async (overrideText) => {
    const msg =
      overrideText !== undefined
        ? overrideText
        : typeof text === "string"
        ? text
        : String(text || "");

    if (!msg.trim()) return;

    appendMessage("user", msg);
    if (overrideText === undefined) setText("");
    setLoading(true);
    try {
      const params = new URLSearchParams();
      params.append("text", msg);
      const res = await fetch("/chat/", { method: "POST", body: params });
      const data = await res.json();
      appendMessage("bot", data.reply || "No reply from server.");
    } catch {
      appendMessage("bot", "Error contacting server.");
    }
    setLoading(false);
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setImage(URL.createObjectURL(file));
    appendMessage("user", "[Sent an image]");

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("file", file);
      formData.append("question", "");
      const res = await fetch("/vision/", { method: "POST", body: formData });
      const data = await res.json();
      appendMessage("bot", data.reply || "No response for image.");
    } catch {
      appendMessage("bot", "Image send failed.");
    }
    setLoading(false);
    e.target.value = null;
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleTextSend();
    }
  };

  const handleVoice = () => {
    if (!recognitionRef.current) {
      alert("Voice recognition not supported in this browser.");
      return;
    }
    if (voiceActive) recognitionRef.current.stop();
    else recognitionRef.current.start();
  };

  return (
    <ThemeProvider theme={theme}>
      <Box
        sx={{
          maxWidth: 600,
          mx: "auto",
          height: "100vh",
          px: 2,
          py: 3,
          display: "flex",
          flexDirection: "column",
          bgcolor: "background.default",
        }}
      >
        <Typography
          variant="h4"
          color="primary.main"
          align="center"
          gutterBottom
          sx={{ userSelect: "none" }}
        >
          ðŸ©º Medical Chatbot
        </Typography>

        <Box
          ref={chatRef}
          sx={{
            flex: 1,
            overflowY: "auto",
            bgcolor: "background.paper",
            p: 2,
            borderRadius: 2,
            boxShadow: 2,
            display: "flex",
            flexDirection: "column",
            mb: 2,
          }}
          aria-live="polite"
        >
          {messages.map((m, i) => (
            <ChatBubble key={i} sender={m.sender} text={m.text} />
          ))}
          {loading && (
            <Box sx={{ display: "flex", justifyContent: "center", mt: 2 }}>
              <CircularProgress color="primary" size={24} />
            </Box>
          )}
        </Box>

        <Box
          sx={{
            display: "flex",
            gap: 1,
            alignItems: "center",
          }}
        >
          <TextField
            multiline
            maxRows={4}
            fullWidth
            placeholder="Type your medical question..."
            value={text}
            onChange={(e) => setText(e.target.value)}
            onKeyDown={handleKeyDown}
            variant="outlined"
            size="medium"
          />
          <IconButton
            color="primary"
            disabled={!text.trim()}
            onClick={() => handleTextSend()}
            aria-label="Send message"
          >
            <SendIcon />
          </IconButton>
          <IconButton
            color={voiceActive ? "error" : "primary"}
            onClick={handleVoice}
            aria-label={voiceActive ? "Stop voice input" : "Start voice input"}
          >
            <MicIcon />
          </IconButton>
          <input
            accept="image/*"
            style={{ display: "none" }}
            id="upload-image-file"
            type="file"
            onChange={handleImageChange}
          />
          <label htmlFor="upload-image-file">
            <IconButton color="primary" component="span" aria-label="Upload Image">
              <ImageIcon />
            </IconButton>
          </label>
        </Box>

        {image && (
          <Box
            sx={{
              mt: 2,
              textAlign: "center",
            }}
          >
            <img
              src={image}
              alt="Uploaded preview"
              style={{
                maxHeight: 140,
                borderRadius: 12,
                boxShadow: "0 0 10px rgba(0,0,0,0.15)",
                userSelect: "none",
              }}
              draggable={false}
            />
          </Box>
        )}

        <Typography
          variant="caption"
          color="text.secondary"
          align="center"
          sx={{ mt: 3, userSelect: "none" }}
        >
          For informational purposes only. Not medical advice.
        </Typography>
      </Box>
    </ThemeProvider>
  );
}
