import React, { useState, useRef, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import { FiSend, FiMic, FiImage } from "react-icons/fi";

// Glassmorphism container style
const glassStyle = {
  background: "rgba(255,255,255,0.20)",
  backdropFilter: "blur(18px)",
  border: "1.5px solid rgba(255,255,255,0.34)",
  boxShadow: "0 8px 32px 0 rgba(31,38,135,0.20)",
};

// Helper to ensure safe string
const toSafeString = val => {
  if (typeof val === "string") return val;
  if (val === null || val === undefined) return "";
  if (React.isValidElement(val)) return "[React element]";
  try { return JSON.stringify(val); } catch { return String(val); }
};

const ChatBubble = ({ sender, text }) => (
  <div
    style={{
      ...glassStyle,
      alignSelf: sender === "user" ? "flex-end" : "flex-start",
      margin: "12px 0",
      padding: "16px 22px",
      borderRadius: "20px",
      maxWidth: "70%",
      color: sender === "user" ? "#fff" : "#222",
      background:
        sender === "user"
          ? "rgba(52,199,89,0.7)"
          : "rgba(245,247,250,0.38)",
      fontSize: "1.13rem",
      wordBreak: "break-word",
      whiteSpace: "pre-line",
      boxShadow: sender === "user" ? "0 4px 20px 0 rgba(52,199,89,0.09)" : "none"
    }}
  >
    {sender === "bot" ? (
      <ReactMarkdown>{toSafeString(text)}</ReactMarkdown>
    ) : (
      toSafeString(text)
    )}
  </div>
);

const Loader = () => (
  <div style={{ width: 28, height: 28, margin: "16px auto", textAlign: "center" }}>
    <div style={{
      width: 28, height: 28,
      border: "4px solid #eee",
      borderTop: "4px solid #55d88b",
      borderRadius: "50%",
      animation: "spin 1s linear infinite"
    }} />
    <style>
      {`@keyframes spin {0%{transform:rotate(0deg);}100%{transform:rotate(360deg);}}`}
    </style>
  </div>
);

export default function App() {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(false);
  const [image, setImage] = useState(null);
  const [voiceActive, setVoiceActive] = useState(false);
  const recognitionRef = useRef(null);
  const chatRef = useRef(null);

  useEffect(() => {
    if (chatRef.current) chatRef.current.scrollTop = chatRef.current.scrollHeight;
  }, [messages, loading]);

  useEffect(() => {
    if ("webkitSpeechRecognition" in window) {
      const rec = new window.webkitSpeechRecognition();
      rec.lang = "en-US";
      rec.continuous = false;
      rec.interimResults = false;
      rec.onstart = () => setVoiceActive(true);
      rec.onend = () => setVoiceActive(false);
      rec.onerror = () => setVoiceActive(false);
      rec.onresult = e => {
        const transcript = e.results[0][0].transcript;
        handleTextSend(transcript);
      };
      recognitionRef.current = rec;
    }
  }, []);

  const appendMessage = (sender, text) =>
    setMessages(prev => [...prev, { sender, text: toSafeString(text) }]);

  const handleTextSend = async (overrideText) => {
    if (overrideText && typeof overrideText === "object" && !(overrideText instanceof String)) return;
    const msg = overrideText !== undefined ? overrideText : text;
    const msgStr = toSafeString(msg);
    if (!msgStr.trim()) return;
    appendMessage("user", msgStr);
    if (overrideText === undefined) setText("");
    setLoading(true);
    try {
      const params = new URLSearchParams();
      params.append("text", msgStr);
      const res = await fetch("/chat/", { method: "POST", body: params });
      const data = await res.json();
      appendMessage("bot", data.reply || "No reply from server.");
    } catch {
      appendMessage("bot", "Error contacting server.");
    }
    setLoading(false);
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setImage(URL.createObjectURL(file));
    appendMessage("user", "[Sent an image]");
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("file", file);
      formData.append("question", "");
      const res = await fetch("/vision/", { method: "POST", body: formData });
      const data = await res.json();
      appendMessage("bot", data.reply || "No response for image.");
    } catch {
      appendMessage("bot", "Image send failed.");
    }
    setLoading(false);
    e.target.value = null;
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleTextSend();
    }
  };

  const handleVoice = () => {
    if (!recognitionRef.current) {
      alert("Voice recognition not supported in this browser.");
      return;
    }
    if (voiceActive) recognitionRef.current.stop();
    else recognitionRef.current.start();
  };

  return (
    <div style={{
      height: "100vh",            // full screen height
      width: "100vw",             // full screen width
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      background: "linear-gradient(115deg, #a1ffce 0%, #faffd1 100%)",
      fontFamily: "Segoe UI, 'Inter', sans-serif",
      position: "fixed",
      top: 0,
      left: 0
    }}>
      {/* Main Chat Container - fills almost all space */}
      <div style={{
        ...glassStyle,
        width: "100%",
        height: "100%",
        maxWidth: "100%",          // remove width limit
        borderRadius: 0,           // remove rounded corners to fill screen
        display: "flex",
        flexDirection: "column",
      }}>
        {/* Header */}
        <div style={{
          textAlign: "center",
          color: "#4caf50",
          fontSize: "2rem",
          fontWeight: 800,
          padding: "20px 0",
          borderBottom: "1px solid rgba(255,255,255,0.2)",
          userSelect: "none"
        }}>
          ðŸ©º Medical Chatbot
        </div>

        {/* Chat Area */}
        <div
          ref={chatRef}
          style={{
            flex: 1,
            overflowY: "auto",
            padding: "20px",
            display: "flex",
            flexDirection: "column"
          }}>
          {messages.map((m, i) => <ChatBubble key={i} sender={m.sender} text={m.text} />)}
          {loading && <Loader />}
        </div>

        {/* Controls */}
        <div style={{
          display: "flex",
          alignItems: "center",
          gap: 10,
          padding: "15px",
          background: "rgba(255,255,255,0.15)",
          borderTop: "1px solid rgba(255,255,255,0.2)"
        }}>
          <textarea
            rows={1}
            value={text}
            onChange={e => setText(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Type your medical question..."
            style={{
              flex: 1,
              borderRadius: 20,
              border: "none",
              padding: "12px 15px",
              fontSize: "1rem",
              background: "rgba(255,255,255,0.8)",
              outline: "none",
              resize: "none"
            }}
          />
          <button
            onClick={() => handleTextSend()}
            disabled={!text.trim()}
            style={{
              color: "#fff",
              background: text.trim() ? "#39b87e" : "#ccc",
              border: "none",
              borderRadius: "50%",
              width: 44, height: 44,
              fontSize: 24,
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              cursor: text.trim() ? "pointer" : "not-allowed"
            }}>
            <FiSend />
          </button>
          <button
            onClick={handleVoice}
            style={{
              color: "#fff",
              background: voiceActive ? "#b83a39" : "#39b87e",
              border: "none",
              borderRadius: "50%",
              width: 44, height: 44,
              fontSize: 24,
              display: "flex",
              alignItems: "center",
              justifyContent: "center"
            }}>
            <FiMic />
          </button>
          <label htmlFor="image-upload"
            style={{
              background: "#39b87e",
              borderRadius: "50%",
              width: 44, height: 44,
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              color: "#fff",
              fontSize: 24,
              cursor: "pointer"
            }}>
            <FiImage />
            <input
              type="file"
              id="image-upload"
              accept="image/*"
              style={{ display: "none" }}
              onChange={handleImageChange}
            />
          </label>
        </div>
      </div>
    </div>
  );
}
