import React, { useState, useRef, useEffect } from "react";
import ReactMarkdown from "react-markdown"; // ✅ Added

// Chat bubble
const ChatBubble = ({ sender, text }) => (
  <div
    style={{
      maxWidth: "70%",
      padding: "10px 15px",
      margin: "8px",
      borderRadius: "18px",
      backgroundColor: sender === "user" ? "#4caf50" : "#ececec",
      color: sender === "user" ? "white" : "#222",
      alignSelf: sender === "user" ? "flex-end" : "flex-start",
      whiteSpace: "pre-line",
    }}
  >
    {sender === "bot" ? (
      <ReactMarkdown className="markdown">{text}</ReactMarkdown> // ✅ Markdown rendering for bot
    ) : (
      text
    )}
  </div>
);

// Loading spinner
const Loader = () => (
  <div
    style={{
      border: "4px solid #f3f3f3",
      borderTop: "4px solid #4caf50",
      borderRadius: "50%",
      width: "20px",
      height: "20px",
      animation: "spin 1s linear infinite",
      margin: "12px auto",
    }}
  />
);

const spinnerStyle = `
  @keyframes spin {
    0% { transform: rotate(0deg);}
    100% { transform: rotate(360deg);}
  }
`;

export default function App() {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(false);
  const [image, setImage] = useState(null);
  const [voiceActive, setVoiceActive] = useState(false);
  const recognitionRef = useRef(null);
  const chatRef = useRef(null);

  // Scroll to bottom on new message
  useEffect(() => {
    if (chatRef.current) chatRef.current.scrollTop = chatRef.current.scrollHeight;
  }, [messages, loading]);

  // Setup voice recognition
  useEffect(() => {
    if ("webkitSpeechRecognition" in window) {
      const rec = new window.webkitSpeechRecognition();
      rec.lang = "en-US";
      rec.continuous = false;
      rec.interimResults = false;
      rec.onstart = () => setVoiceActive(true);
      rec.onend = () => setVoiceActive(false);
      rec.onerror = () => setVoiceActive(false);
      rec.onresult = async (e) => {
        const transcript = e.results[0][0].transcript;
        appendMessage("user", transcript);
        await handleTextSend(transcript);
      };
      recognitionRef.current = rec;
    }
  }, []);

  const appendMessage = (sender, text) =>
    setMessages((prev) => [...prev, { sender, text }]);

  // Send text message
  const handleTextSend = async (overrideText) => {
  const msg = overrideText !== undefined ? overrideText : text;
  const msgStr = typeof msg === "string" ? msg : String(msg);
  if (!msgStr.trim()) return;

  // ✅ Always add the user's message to the history
  appendMessage("user", msgStr);

  // Clear the input if it was typed
  if (overrideText === undefined) {
    setText("");
  }

  setLoading(true);
  try {
    const params = new URLSearchParams();
    params.append("text", msgStr);
    const res = await fetch("/chat/", { method: "POST", body: params });
    const data = await res.json();

    // Append bot reply after user message
    appendMessage("bot", data.reply || "No reply from server.");
  } catch {
    appendMessage("bot", "Error contacting server.");
  }
  setLoading(false);
};


  // Send image
  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setImage(URL.createObjectURL(file));
    appendMessage("user", "[Sent an image]");
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("file", file);
      formData.append("question", "");
      const res = await fetch("/vision/", { method: "POST", body: formData });
      const data = await res.json();
      appendMessage("bot", data.reply || "No response for image.");
    } catch {
      appendMessage("bot", "Image send failed.");
    }
    setLoading(false);
    e.target.value = null;
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleTextSend();
    }
  };

  const handleVoice = () => {
    if (!recognitionRef.current) {
      alert("Voice recognition is not supported in this browser.");
      return;
    }
    if (voiceActive) recognitionRef.current.stop();
    else recognitionRef.current.start();
  };

  return (
    <>
      <style>{spinnerStyle}</style>
      <div
        style={{
          fontFamily: "Segoe UI, sans-serif",
          maxWidth: 600,
          margin: "0 auto",
          display: "flex",
          flexDirection: "column",
          height: "100vh",
          padding: 20,
          background: "#f5f7fa",
        }}
      >
        <h2 style={{ textAlign: "center", color: "#4caf50" }}>🩺 Medical Chatbot</h2>

        {/* Chat display */}
        <div
          ref={chatRef}
          style={{
            flex: 1,
            overflowY: "auto",
            background: "white",
            padding: 12,
            borderRadius: 8,
            boxShadow: "0 0 10px rgba(0,0,0,0.1)",
            display: "flex",
            flexDirection: "column"
          }}
          aria-live="polite"
        >
          {messages.map((m, i) => (
            <ChatBubble key={i} sender={m.sender} text={m.text} />
          ))}
          {loading && <Loader />}
        </div>

        {/* Input controls */}
        <div style={{ display: "flex", alignItems: "center", gap: 8, marginTop: 12 }}>
          <textarea
            rows={2}
            value={text}
            onChange={(e) => setText(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Type your medical question..."
            style={{
              flex: 1,
              borderRadius: 18,
              border: "1px solid #ccc",
              padding: 12,
              fontSize: 16,
              resize: "none",
            }}
          />
          <button
            onClick={handleTextSend}
            disabled={!text.trim()}
            style={{
              background: text.trim() ? "#4caf50" : "#9e9e9e",
              color: "white",
              border: "none",
              borderRadius: "50%",
              width: 44,
              height: 44,
              fontSize: 20,
              cursor: text.trim() ? "pointer" : "default",
            }}
            aria-label="Send message"
          >
            ➤
          </button>
          <button
            onClick={handleVoice}
            style={{
              background: voiceActive ? "#f44336" : "#4caf50",
              color: "white",
              border: "none",
              borderRadius: "50%",
              width: 44,
              height: 44,
              fontSize: 20,
              cursor: "pointer",
            }}
            aria-label={voiceActive ? "Stop voice input" : "Start voice input"}
          >
            🎙
          </button>
          <label
            htmlFor="image-upload"
            style={{
              background: "#4caf50",
              borderRadius: "50%",
              width: 44,
              height: 44,
              color: "white",
              fontSize: 20,
              cursor: "pointer",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              userSelect: "none"
            }}
            title="Upload Image"
          >
            🖼
          </label>
          <input
            type="file"
            id="image-upload"
            accept="image/*"
            style={{ display: "none" }}
            onChange={handleImageChange}
          />
        </div>

        {image && (
          <div style={{ marginTop: 10, textAlign: "center" }}>
            <img
              src={image}
              alt="Selected"
              style={{
                maxHeight: 120,
                borderRadius: 12,
                boxShadow: "0 0 8px rgba(0,0,0,0.15)",
              }}
            />
          </div>
        )}

        <footer
          style={{
            textAlign: "center",
            color: "#777",
            fontSize: 12,
            marginTop: 16,
            userSelect: "none",
          }}
        >
          For informational purposes only. Not medical advice.
        </footer>
      </div>
    </>
  );
}
