import React, { useState, useRef, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import { FiSend, FiMic, FiImage } from "react-icons/fi";

// Glassmorphism style variables
const glassStyle = {
  background: "rgba(255,255,255,0.20)",
  backdropFilter: "blur(18px)",
  border: "1.5px solid rgba(255,255,255,0.34)",
  boxShadow: "0 8px 32px 0 rgba(31,38,135,0.20)",
};

// Helper to ensure safe string
const toSafeString = val => {
  if (typeof val === "string") return val;
  if (val === null || val === undefined) return "";
  if (React.isValidElement(val)) return "[React element]";
  try { return JSON.stringify(val); } catch { return String(val); }
};

const ChatBubble = ({ sender, text }) => (
  <div
    style={{
      ...glassStyle,
      alignSelf: sender === "user" ? "flex-end" : "flex-start",
      margin: "12px 0",
      padding: "16px 22px",
      borderRadius: "20px",
      maxWidth: "70%",
      color: sender === "user" ? "#fff" : "#222",
      background:
        sender === "user"
          ? "rgba(52,199,89,0.7)"
          : "rgba(245,247,250,0.38)",
      fontSize: "1.13rem",
      wordBreak: "break-word",
      whiteSpace: "pre-line",
      boxShadow: sender === "user" ? "0 4px 20px 0 rgba(52,199,89,0.09)" : "none"
    }}
  >
    {sender === "bot" ? (
      <ReactMarkdown>{toSafeString(text)}</ReactMarkdown>
    ) : (
      toSafeString(text)
    )}
  </div>
);

const Loader = () => (
  <div style={{ width: 28, height: 28, margin: "16px auto", textAlign: "center" }}>
    <div style={{
      width: 28, height: 28,
      border: "4px solid #eee",
      borderTop: "4px solid #55d88b",
      borderRadius: "50%",
      animation: "spin 1s linear infinite"
    }} />
    <style>
      {`@keyframes spin {0%{transform:rotate(0deg);}100%{transform:rotate(360deg);}}`}
    </style>
  </div>
);

export default function App() {
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(false);
  const [image, setImage] = useState(null);
  const [voiceActive, setVoiceActive] = useState(false);
  const recognitionRef = useRef(null);
  const chatRef = useRef(null);

  useEffect(() => {
    if (chatRef.current) chatRef.current.scrollTop = chatRef.current.scrollHeight;
  }, [messages, loading]);

  useEffect(() => {
    if ("webkitSpeechRecognition" in window) {
      const rec = new window.webkitSpeechRecognition();
      rec.lang = "en-US";
      rec.continuous = false;
      rec.interimResults = false;
      rec.onstart = () => setVoiceActive(true);
      rec.onend = () => setVoiceActive(false);
      rec.onerror = () => setVoiceActive(false);
      rec.onresult = e => {
        const transcript = e.results[0][0].transcript;
        handleTextSend(transcript);
      };
      recognitionRef.current = rec;
    }
  }, []);

  // Safe message append
  const appendMessage = (sender, text) =>
    setMessages(prev => [...prev, { sender, text: toSafeString(text) }]);

  // Send text/voice message
  const handleTextSend = async (overrideText) => {
    if (overrideText && typeof overrideText === "object" && !(overrideText instanceof String)) return;
    const msg = overrideText !== undefined ? overrideText : text;
    const msgStr = toSafeString(msg);
    if (!msgStr.trim()) return;
    appendMessage("user", msgStr);
    if (overrideText === undefined) setText("");
    setLoading(true);
    try {
      const params = new URLSearchParams();
      params.append("text", msgStr);
      const res = await fetch("/chat/", { method: "POST", body: params });
      const data = await res.json();
      appendMessage("bot", data.reply || "No reply from server.");
    } catch {
      appendMessage("bot", "Error contacting server.");
    }
    setLoading(false);
  };

  // Send image
  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setImage(URL.createObjectURL(file));
    appendMessage("user", "[Sent an image]");
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("file", file);
      formData.append("question", "");
      const res = await fetch("/vision/", { method: "POST", body: formData });
      const data = await res.json();
      appendMessage("bot", data.reply || "No response for image.");
    } catch {
      appendMessage("bot", "Image send failed.");
    }
    setLoading(false);
    e.target.value = null;
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleTextSend();
    }
  };

  const handleVoice = () => {
    if (!recognitionRef.current) {
      alert("Voice recognition not supported in this browser.");
      return;
    }
    if (voiceActive) recognitionRef.current.stop();
    else recognitionRef.current.start();
  };

  return (
    // Full viewport glass background
    <div style={{
      minHeight: "100vh",
      width: "100vw",
      position: "fixed",
      top: 0,
      left: 0,
      background: "linear-gradient(115deg, #a1ffce 0%, #faffd1 100%)",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      fontFamily: "Segoe UI, 'Inter', system-ui, sans-serif"
    }}>
      <div style={{
        ...glassStyle,
        width: "100vw",
        maxWidth: 720,
        height: "96vh",
        margin: "auto",
        padding: "36px 0 0 0",
        borderRadius: "34px",
        boxShadow: "0 8px 44px 0 rgba(31,38,135,0.30)",
        display: "flex",
        flexDirection: "column",
        overflow: "hidden"
      }}>
        {/* Header */}
        <div style={{
          textAlign: "center",
          color: "#4caf50",
          fontSize: "2.6rem",
          fontWeight: 900,
          letterSpacing: "-1.5px",
          marginBottom: 0,
          userSelect: "none"
        }}>
          ðŸ©º Medical Chatbot
        </div>
        {/* Chat Scroll Area */}
        <div
          ref={chatRef}
          style={{
            flex: 1,
            overflowY: "auto",
            padding: "24px 36px 6px 36px",
            display: "flex",
            flexDirection: "column",
          }}>
          {messages.map((m, i) => (
            <ChatBubble key={i} sender={m.sender} text={m.text} />
          ))}
          {loading && <Loader />}
        </div>
        {/* Controls */}
        <div style={{
          display: "flex", alignItems: "center", gap: 14,
          padding: "22px 36px 18px 36px",
          background: "none"
        }}>
          <textarea
            rows={2}
            value={text}
            onChange={e => setText(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Type your medical question..."
            style={{
              flex: 1,
              borderRadius: 20,
              border: "1.5px solid #e5e5e5",
              padding: "14px 17px",
              fontSize: "1.07rem",
              fontFamily: "inherit",
              background: "rgba(255,255,255,0.7)",
              boxShadow: "0 3px 14px 0 rgba(34,255,180,0.08)",
              outline: "none",
              resize: "none",
              color: "#222",
            }}
          />
          <button
            onClick={() => handleTextSend()}
            disabled={!text.trim()}
            style={{
              color: "#fff",
              background: text.trim() ? "#39b87e" : "#ccd9cf",
              border: "none",
              borderRadius: "50%",
              width: 44, height: 44,
              fontSize: 28,
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              cursor: text.trim() ? "pointer" : "not-allowed"
            }}>
            <FiSend />
          </button>
          <button
            onClick={handleVoice}
            style={{
              color: "#fff",
              background: voiceActive ? "#b83a39" : "#39b87e",
              border: "none",
              borderRadius: "50%",
              width: 44, height: 44,
              fontSize: 28,
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              cursor: "pointer"
            }}>
            <FiMic />
          </button>
          <label htmlFor="image-upload" style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            background: "#39b87e",
            borderRadius: "50%",
            width: 44, height: 44,
            color: "#fff",
            fontSize: 28,
            cursor: "pointer"
          }}>
            <FiImage />
            <input
              type="file"
              id="image-upload"
              accept="image/*"
              style={{ display: "none" }}
              onChange={handleImageChange}
            />
          </label>
        </div>
        {/* Uploaded image preview */}
        {image && (
          <div style={{ marginTop: 2, marginBottom: 6, textAlign: "center" }}>
            <img
              src={image}
              alt="Selected"
              style={{
                maxHeight: 110,
                borderRadius: 16,
                boxShadow: "0 0 10px 2px #39b87e55",
              }}
            />
          </div>
        )}
        {/* Footer */}
        <div style={{
          textAlign: "center",
          color: "#888",
          fontSize: 13,
          marginBottom: 6,
          opacity: 0.86,
          userSelect: "none"
        }}>
          For informational purposes only. Not medical advice.<br />
          Â© {new Date().getFullYear()} Your Brand
        </div>
      </div>
    </div>
  );
}
